import React,{useState} from 'react'
import ExpenseItem from './ExpenseItem';
import Filter from './Filter'
import Card from "../UI/Card"
import "./Expenses.css"

const Expenses = (props) => {
const [filterYear, setFilterYear] = useState('2020')
const [infoText, setInfoText] = useState('2019, 2020 & 2022')

const filterChangeHandler = (selectedYear) => {
setFilterYear(selectedYear)
if (selectedYear === '2019'){
  setInfoText('2020, 2021 & 2022')
}else if (selectedYear === '2020'){
  setInfoText('2019, 2021 & 2022')
}else if (selectedYear === '2021'){
  setInfoText('2019, 2020 & 2022')
}else{
  setInfoText('2019, 2020 & 2021')
}
}

  return (
    <div>
    <Card className="expenses">
    <Filter selected={filterYear}  onChangeFilter={filterChangeHandler}/>
    <p>Data for years {infoText} is hidden.</p>
      <ExpenseItem
        title={props.item[0].title}
        amount={props.item[0].amount}
        date={props.item[0].date}
      />
      <ExpenseItem
        title={props.item[1].title}
        amount={props.item[1].amount}
        date={props.item[1].date}
      />
      <ExpenseItem
        title={props.item[2].title}
        amount={props.item[2].amount}
        date={props.item[2].date}
      />
      <ExpenseItem
        title={props.item[3].title}
        amount={props.item[3].amount}
        date={props.item[3].date}
      />
    </Card>
    </div>
  );
};

export default Expenses;
